let counter = 1;

function tambahForm() {
    counter++;
    document.getElementById('counter').value = counter;
    
    const container = document.getElementById('form-container');

    const newForm = document.createElement('div');
    newForm.classList.add('form-group');

    const row = document.createElement('div');
    row.classList.add('row');

    // Input untuk sub_Krit
    const colSubKrit = document.createElement('div');
    colSubKrit.classList.add('col');

    const labelSubKrit = document.createElement('label');
    labelSubKrit.classList.add('form-control-label');
    labelSubKrit.setAttribute('for', 'sub_krit_' + counter);
    labelSubKrit.textContent = 'Sub Kriteria ' + counter;

    const inputSubKrit = document.createElement('input');
    inputSubKrit.setAttribute('type', 'text');
    inputSubKrit.setAttribute('id', 'sub_krit_' + counter);
    inputSubKrit.setAttribute('class', 'form-control');
    inputSubKrit.setAttribute('name', 'sub_krit_' + counter);
    inputSubKrit.setAttribute('placeholder', 'Isikan Sub Kriteria');

    // Input untuk nilai_sub
    const colNilaiSub = document.createElement('div');
    colNilaiSub.classList.add('col-3');

    const labelNilaiSub = document.createElement('label');
    labelNilaiSub.classList.add('form-control-label');
    labelNilaiSub.setAttribute('for', 'nilai_sub_' + counter);
    labelNilaiSub.textContent = 'Nilai ' + counter;

    const inputNilaiSub = document.createElement('input');
    inputNilaiSub.setAttribute('type', 'number');
    inputNilaiSub.setAttribute('id', 'nilai_sub_' + counter);
    inputNilaiSub.setAttribute('class', 'form-control');
    inputNilaiSub.setAttribute('name', 'nilai_sub_' + counter);
    inputNilaiSub.setAttribute('placeholder', counter);

    colSubKrit.appendChild(labelSubKrit);
    colSubKrit.appendChild(inputSubKrit);

    colNilaiSub.appendChild(labelNilaiSub);
    colNilaiSub.appendChild(inputNilaiSub);

    row.appendChild(colSubKrit);
    row.appendChild(colNilaiSub);

    newForm.appendChild(row);

    container.appendChild(newForm);

}

function hapusForm() {
    if (counter > 1) {
        const container = document.getElementById('form-container');
        container.removeChild(container.lastChild);
        counter--;
    }
}